import { useState, useRef } from 'react';
import axios from 'axios'
import './App.css';

function App() {

  const input = useRef()


  async function SearchCity(){

    const city = input.current.value
    const key = '2fe81c6ba071b05eb0b6e0c15b969819'
    const adress = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${key}`

    const dataCity = await axios.get(adress)

    console.log(dataCity)

  }


  return (

    <div className="App">

      <div className='formCity'>

        <input type='text' placeholder='Busque sua cidade aqui' ref={input} className='inputCity'><button onClick={SearchCity}>Buscar</button></input>


      </div>

    </div>
  );
}

export default App;
